changeSet.sql:
CREATE TABLE survey_metadata ( id bigserial PRIMARY KEY, file_id bigint UNIQUE NOT NULL, location text NOT NULL, survey_date date NOT NULL, source text, name text, coll_Name text, authority text, habitat text, comments text, coll_date text, status text, xyz_file text, vessel text, z_datum text, z_app text, z_tech text, geom geometry(Geometry,4326) CHECK (st_isvalid(geom)),
CONSTRAINT survey_metadata_file_fk FOREIGN KEY (file_id) REFERENCES indexed_file (id) MATCH SIMPLE ON UPDATE CASCADE ON DELETE CASCADE );
CREATE INDEX survey_metadata_gist_idx ON survey_metadata USING GIST (geom);
