changeSet.sql:
CREATE TABLE cpr_phyto_taxon_changelog (
  id bigserial PRIMARY KEY,
  taxon_name text NOT NULL UNIQUE,
  taxon_start_date text,
  first_occurrence text,
  parent_taxon_name text,
  training text,
  comments text
);

CREATE INDEX cpr_phyto_taxon_changelog_taxon_name_idx ON cpr_phyto_taxon_changelog (taxon_name);

DROP TABLE IF EXISTS cpr_phyto_htg_map CASCADE ;
DROP TABLE IF EXISTS cpr_phyto_genus_map CASCADE ;
DROP TABLE IF EXISTS cpr_phyto_species_map CASCADE ;
DROP TABLE IF EXISTS cpr_phyto_raw_map CASCADE ;

DROP VIEW IF EXISTS cpr_phyto_htg_map CASCADE;
CREATE VIEW cpr_phyto_htg_map AS
  SELECT * FROM cpr_phyto_htg ;

DROP VIEW IF EXISTS cpr_phyto_genus_map CASCADE;
CREATE VIEW cpr_phyto_genus_map AS
  SELECT * FROM cpr_phyto_genus ;

DROP VIEW IF EXISTS cpr_phyto_species_map CASCADE;
CREATE VIEW cpr_phyto_species_map AS
  SELECT * FROM cpr_phyto_species ;

DROP VIEW IF EXISTS cpr_phyto_raw_map CASCADE;
CREATE VIEW cpr_phyto_raw_map AS
  SELECT * FROM cpr_phyto_raw ;

DROP VIEW IF EXISTS cpr_zoop_htg_map CASCADE;
CREATE VIEW cpr_zoop_htg_map AS
  SELECT * FROM cpr_zoop_htg ;

DROP VIEW IF EXISTS cpr_zoop_genus_map CASCADE;
CREATE VIEW cpr_zoop_genus_map AS
  SELECT * FROM cpr_zoop_genus ;

DROP VIEW IF EXISTS cpr_zoop_copes_map CASCADE;
CREATE VIEW cpr_zoop_copes_map AS
  SELECT * FROM cpr_zoop_copes ;

DROP VIEW IF EXISTS cpr_zoop_noncopes_map CASCADE;
CREATE VIEW cpr_zoop_noncopes_map AS
  SELECT * FROM cpr_zoop_noncopes ;

DROP VIEW IF EXISTS cpr_zoop_raw_map CASCADE;
CREATE VIEW cpr_zoop_raw_map AS
  SELECT * FROM cpr_zoop_raw ;

DROP TABLE cpr_ind CASCADE ;
CREATE TABLE cpr_ind ( id bigserial PRIMARY KEY, "Latitude" double precision NOT NULL, "Longitude" double precision NOT NULL, "SampleDateUTC" timestamp without time zone NOT NULL, "Year" integer NOT NULL, "Month" integer NOT NULL, "Day" integer NOT NULL, "Time_24hr" text NOT NULL, "Region" text, "Route" text, "WaterDepth_m" real, "SST_C" real, "ChlorophyllMonthlyClimatology_mg_m3" real, "ChlorophyllSatellite_mg_m3" real, "CopepodAbundance_m3" real, "ZoopAbundance_m3" real, "AvgTotalLengthCopepod_mm" real, "NoCopepodSpecies_sample" real, "ShannonCopepodDiversity" real, "CopepodEvenness" real, "HerbivoreCarnivoreCopepodRatio" real, "TotalSalps_m3" real, "TotalLarvacean_m3" real, "TotalCladoceran_m3" real, "Biomass_mgCarbon_m3" real, "AbundancePhyto_cells_m3" real, "PhytoBiomass_Carbon_pg_L" real, "DiatomDinoflagellateRatio" real, "AvgCellVolume_um3" real, "NoPhytoSpecies_sample" real, "ShannonPhytoDiversity" real, "PhytoEveness" real, "NoDiatomSpecies_sample" real, "ShannonDiatomDiversity" real, "DiatomEvenness" real, "NoDinoSpecies_sample" real, "ShannonDinoDiversity" real, "DinoflagellateEvenness" real, geom geometry(Geometry,4326) CHECK (st_isvalid(geom)) );
CREATE INDEX cpr_ind_gist_idx ON cpr_ind USING GIST (geom);

DROP VIEW IF EXISTS cpr_ind_map CASCADE;
CREATE VIEW cpr_ind_map AS
  SELECT * FROM cpr_ind ;

