DROP TABLE nrs_ind CASCADE ;
CREATE TABLE nrs_ind ( id bigserial PRIMARY KEY, "NRScode" text, "Station" text NOT NULL, "Latitude" double precision NOT NULL, "Longitude" double precision NOT NULL, "SampleDateLocal" timestamp without time zone NOT NULL, "Year" integer NOT NULL, "Month" integer NOT NULL, "Day" integer NOT NULL, "Time_24hr" text NOT NULL, "SST_C" real, "ChlorophyllMonthlyClimatology_mg_m3" real, "ChlorophyllSatellite_mg_m3" real, "ZoopAbundance_m3" real, "CopepodAbundance_m3" real, "AvgTotalLengthCopepod_mm" real, "HerbivoreCarnivoreCopepodRatio" real, "NoCopepodSpecies_Sample" real, "ShannonCopepodDiversity" real, "CopepodEvenness" real, "ZooBiomass_mgCarbon_m3" real, "Salinity_psu" real, "Silicate_umol_L" real, "Nitrate_umol_L" real, "Ammonium_umol_L" real, "Phosphate_umol_L" real, "TotalCarbon_umol_kg" real, "TotalAlkalinity_umol_kg" real, "Oxygen_umol_L" real, "PhytoBiomass_Carbon_pg_L" real, "AbundancePhyto_cells_L" real, "DiatomDinoflagellateRatio" real, "AvgCellVolume_um3" real, "NoPhytoSpecies_Sample" real, "ShannonPhytoDiversity" real, "PhytoEvenness" real, "NoDiatomSpecies_Sample" real, "ShannonDiatomDiversity" real, "DiatomEvenness" real, "NoDinoSpecies_Sample" real, "ShannonDinoDiversity" real, "DinoflagellateEvenness" real, geom geometry(Geometry,4326) CHECK (st_isvalid(geom)) );
CREATE INDEX nrs_ind_gist_idx ON nrs_ind USING GIST (geom);

